mapper.select:
SELECT *
FROM attach_file
WHERE 1
  AND relTypeCode = #{relTypeCode}
  AND relId = #{relId}
  AND typeCode = #{typeCode}
  AND type2Code = #{type2Code}
  AND fileExtTypeCode = 'img'
ORDER BY fileNo ASC
LIMIT 0, 1;

SELECT *
FROM attach_file
WHERE 1
  AND relTypeCode = #{relTypeCode}
  AND relId = #{relId}
  AND typeCode = #{typeCode}
  AND type2Code = #{type2Code}
  AND fileNo = #{fileNo}

SELECT *
FROM attach_file
WHERE 1
  AND relTypeCode = #{relTypeCode}
  AND relId = #{relId}

  AND typeCode = #{typeCode}


  AND type2Code = #{type2Code}

ORDER BY typeCode, type2Code, fileNo

mapper.update:
UPDATE `attach_file`

	updateDate = NOW(),
	relId = #{relId}

WHERE id = #{id}

mapper.delete:
DELETE
FROM `attach_file`
WHERE relTypeCode = #{relTypeCode}
  AND relId = #{relId}
